import { createApi, fetchBaseQuery } from '@reduxjs/toolkit/query/react'
import { Word } from '../types/types'

export const wordsAPI = createApi({
    reducerPath: 'wordsApi',
    baseQuery: fetchBaseQuery({baseUrl: 'http://localhost:3002/'}),
    tagTypes: ['words'],
    endpoints: (builder) => ({
        getAllWords: builder.query<any, void>({
            query: () =>  `words`,
            providesTags: (result) =>
                result
                ? [
                    ...result.map(({ id }: any) => ({ type: 'words', id })),
                    { type: 'words', id: 'LIST' },
                    ]
                : [{ type: 'words', id: 'LIST' }],  
                transformResponse: (resp: Word[]) => resp.sort((a: Word, b: Word) => a.id - b.id)
        }),
        getWordsByGroup: builder.query<any, any>({
            query: (group) =>  `words/group/${group}`,
            providesTags: (result) =>
                result
                ? [
                    ...result.map(({ id }: any) => ({ type: 'words', id })),
                    { type: 'words', id: 'LIST' },
                    ]
                : [{ type: 'words', id: 'LIST' }],
        }),
        setWord: builder.mutation<any, any>({
            query: (body) => ({
                url: `words`,
                method: 'POST',
                headers: {
                    'Accept': `text/html,application/xhtml+xml,application/xml;q=0.9,image/avif,image/webp,image/apng,* /*;q=0.8,application/signed-exchange;v=b3;q=0.9`,
                    "Content-Type": "multipart/form-data; boundary=----WebKitFormBoundary6rw38Byd6jlFsu0A"
                },
                body
            }),
            invalidatesTags: ['words']
        }),
        putWord: builder.mutation<any, any>({
            query: (body) => ({
                url: `words`,
                method: 'PUT',
                body
            }),
            invalidatesTags: ['words']
        }),
        deleteWord: builder.mutation<any, any>({
            query: (id) => ({
                url: `words`,
                method: 'DELETE',
                body: {id}
            }),
            invalidatesTags: ['words']
        }),
    })
})

export const { useGetAllWordsQuery, useSetWordMutation, useGetWordsByGroupQuery, usePutWordMutation, useDeleteWordMutation } = wordsAPI
