import { createApi, fetchBaseQuery } from '@reduxjs/toolkit/query/react'
import { Word } from '../types/types'

export const vocabularyAPI = createApi({
    reducerPath: 'vocabularyApi',
    baseQuery: fetchBaseQuery({baseUrl: 'http://localhost:3002/'}),
    tagTypes: ['vocabulary'],
    endpoints: (builder) => ({
        getVocabulary: builder.query<any, any>({
            query: (id: any) =>  `vocabulary/${id}`,
            providesTags: (result, error, id) => [{ type: 'vocabulary', id }],
        }),
        getUnlerned: builder.query<any, any>({ //может надо разбить на отдельные методы лучше
            query: (req) =>  `/vocabulary/${req.userId}/unlerned/${req.method}/group/${req.groupId}`,
            providesTags: (result, error, id) => [{ type: 'vocabulary', id }],
        }),
        getUnlernedSpell: builder.query<Word, any>({ //может надо разбить на отдельные методы лучше
            query: (req) =>  `/vocabulary/${req.userId}/unlerned/spelling/group/${req.groupId}`,
            providesTags: (result, error, id) => [{ type: 'vocabulary', id }],
            transformResponse: (resp: Word) => ({...resp, trueVariant: resp.eng, eng: resp.eng.split('').sort(() => Math.random() - 0.5).join('')})
        }),
        setVocabulary: builder.mutation<any, any>({
            query: (body) => ({
                url: `vocabulary/${body.userId}/${body.method}`,
                method: 'PUT',
                body
            }),
            invalidatesTags: ['vocabulary']
        }),
        wrongAnswer: builder.mutation<any, any>({
            query: () => ({
                url: `vocabulary/wrong`,
                method: 'PUT'
            }),
            invalidatesTags: ['vocabulary']
        }),
        getGroupProgess: builder.query<{english: number, russian: number, spelling: number, auding: number}, {groupId: number, userId: number}>({
            query: (body) =>  `groups/${body.groupId}/progress/${body.userId}`,
            providesTags: ['vocabulary'], 
        }),
    })
})

export const { useGetVocabularyQuery, useSetVocabularyMutation, useGetUnlernedQuery, useGetUnlernedSpellQuery, useWrongAnswerMutation, useGetGroupProgessQuery } = vocabularyAPI
