import { Link, useParams } from 'react-router-dom'
import { Progress } from 'flowbite-react';
import { TextTitle } from '../../../app/types/types';
import { useAppSelector } from '../../../app/hooks/hooks';
import { useGetGroupProgessQuery } from '../../../app/API/vocabularyAPI';
import { getUserId } from '../../../app/clientAPI/userAPI';
import { useEffect, useState } from 'react';
import { getVocabulary } from '../../../app/clientAPI/vocabularyAPI';
import getGroupProgress from '../../../app/fns/groupFns';
import { WORD_IDS } from '../../../app/variables/dbVariables';
import GroupProgress from '../../GroupPage/GroupProgress';

export default function TextCard(props: TextTitle){
    return (
        <Link to={`/texts/${props.id}`} className="block p-6 bg-white rounded-lg border border-gray-200 shadow-md hover:bg-gray-100 dark:bg-gray-800 dark:border-gray-700 dark:hover:bg-gray-700">
            <h6 className="break-words !text-lg h-16 text-center mb-2 text-2xl font-bold tracking-tight text-gray-900 dark:text-white">
                {props.title}
            </h6>
            <GroupProgress total id={ props.id_group } />
        </Link>    
    )
}
