import { useAppSelector } from '../../../app/hooks/hooks';
import { useGetGroupProgessQuery } from '../../../app/API/vocabularyAPI';

export default function TextCard(props: TextTitle){
    const { id: userId } = useAppSelector((state: RootState) => state.userData)
    // const { data: progress, isSuccess, isError } = useGetGroupProgessQuery({userId, groupId: props.id_group}) //Есть случаи при загрузке group_id === null? Хотя он и не продразумевается но обрабоат его надо
