import { createAsyncThunk, createSlice } from '@reduxjs/toolkit'
import { RootState } from '../store'
import { GroupTitle, User } from '../types/types'

export const getAllGroupsTitlesThunk = createAsyncThunk<GroupTitle[], void, { state: RootState }>(
    'Thunk: getAllGroupsTitles',
    async function(_) {
        const response = await fetch(`http://localhost:3002/groups/titles`)
        const data = await response.json()
        return data
    }
)

const initialState: GroupTitle[] = []
export const groupsSlice = createSlice<GroupTitle[], {}>({
    name: 'Slice: groups',
    initialState: initialState,
    reducers: {},
    extraReducers: (builder) => {
        builder.addCase(getAllGroupsTitlesThunk.fulfilled, (_, action) => action.payload)
    }
})
