import { useEffect, useState } from "react";
import { SERVER_URL } from "../../app/variables/dbVariables";
import GroupProgress from "./GroupProgress";

export default function GroupProgressWrapper( props: { all?: boolean, id_group?: number, id_text?: number, id_audio?: number, id_video?: number} ){
    const [id_group, setId_group] = useState<number | null>(null)
    useEffect(()=>{
        if(props.groupId){
            setId_group(props.groupId)
        }

        if(props.textId || props.audioId || props.videoId){
            let url = ''
            if(props.textId){
                url = `/texts/${props.groupId}/ref-id-group`
            } else if(props.audioId) {
                url = `/audios/${props.groupId}/ref-id-group`
            } else if(props.videoId) {
                url = `/videos/${props.groupId}/ref-id-group`
            }
            fetch(SERVER_URL + url)
            .then( response => {
                return response.json()
            })
            .then(response =>{
                if(response.id_group){
                    setId_group(response.id_group)
                }
            })
            .catch(err => {
                setId_group(null)
            })
        }

    }, [props])
    return (
       < GroupProgress all={props.all} id_group={id_group} />
    )
}
