import { createAsyncThunk, createSlice } from '@reduxjs/toolkit'
import { GroupTitle } from '../types/types'

export const getAllGroupsTitlesThunk = createAsyncThunk<GroupTitle[]>(
    'Thunk: getAllGroupsTitles',
    async function() {
        const response = await fetch(`http://localhost:3002/groups/titles`) //Потом здесб добавить пагинацию
        return await response.json()
    }
)

const initialState: GroupTitle[] = []
export const groupsSlice = createSlice<GroupTitle[], {}>({
    name: 'Slice: groups',
    initialState: initialState,
    reducers: {},
    extraReducers: (builder) => {
        builder.addCase(getAllGroupsTitlesThunk.fulfilled, (_, action) => action.payload)
    }
})
