import { createAsyncThunk, createSlice, PayloadAction } from '@reduxjs/toolkit'
import { RootState } from '../store'
import { User } from '../types/types'

export type Vocabulary = {
    english: number[],
    russian: number[],
    spelling:  number[],
    auding: number[],
}
const initialState: Vocabulary = {
    english: [],
    russian: [],
    spelling: [],
    auding: []
}
export const getVocabularyThunk = createAsyncThunk<Vocabulary, void, { state: RootState }>(
    'Thunk: getVocabulary',
    async function(_, thunkAPI) {
        const { user } = thunkAPI.getState() as { user: User}
        const response = await fetch(`http://localhost:3002/vocabulary/${user.id}`)
        const data = await response.json()
        return data
    }
)
export const vocabularySlice = createSlice<Vocabulary, {}>({
    name: 'Slice: Vocabulary',
    initialState: initialState,
    reducers: {
        pushEnglish: (state: Vocabulary, payload: PayloadAction) => {
            state.english.push();
        },
    },
    extraReducers: (builder) => {
        builder.addCase(getVocabularyThunk.fulfilled, (_, action) => action.payload)
        builder.addCase(getVocabularyThunk.rejected, (_, __) => initialState)
    }
})

export const english = (state: RootState) => state.vocabulary.english;
export const russian = (state: RootState) => state.vocabulary.russian;
export const spelling = (state: RootState) => state.vocabulary.spelling;
export const auding = (state: RootState) => state.vocabulary.auding;
