import { useEffect, useState } from "react";

export function ReferencesGroup(props : { groupId: number }){
    const [ references, setReferences ] = useState()
    useEffect(()=>{
        if(typeof props.groupId === 'number' && props.groupId > 0){
            fetch(`http://localhost:3002/groups/${props.groupId}/references`)
            .then( response => {
                return response.json()
            })
            .then((response ) =>{
                if(response.hasOwnProperty('group') || response.hasOwnProperty('text') || response.hasOwnProperty('audio') || response.hasOwnProperty('video')){
                    setReferences(response)
                }
            })
            .catch(err => {
               console.error('osibka')
            })
        }
    }, [ props ])
