import { useEffect } from "react"
import { useGetTextsQuery, useGetTitleTextsQuery } from "../../../app/API/textsAPI"
import { getAllGlobalTextsTitlesThunk } from "../../../app/API/textSliceAPI"
import { useAppDispatch, useAppSelector } from "../../../app/hooks/hooks"
import { RootState } from "../../../app/store"
import { Text, TextTitle } from "../../../app/types/types"
import TextCard from "../TextCard/TextCard"

export default function Texts(){
    const dispatch = useAppDispatch()
    const { data: texts, isSuccess, isError } = useGetTitleTextsQuery()
    const texts2 = useAppSelector((state: RootState) => state.texts)
    useEffect(()=>{
        dispatch(getAllGlobalTextsTitlesThunk())
    })
    console.log(texts2)
    return (
        <div className='grid grid-cols-1 gap-4 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 xl:grid-cols-5 2xl:grid-cols-5'>
            {isSuccess && texts.map((el: TextTitle, i: number) => {
                return <TextCard {...el} key={i} />
            })}
            {isError || isSuccess && texts.length === 0 &&
            <div>
                <h1>Какая-то ошибка...</h1>
            </div>}
        </div>
    )
}
