import { Checkbox, Table, TextInput } from "flowbite-react"
import { useState } from "react"
import { useGetAllWordsQuery } from "../../app/API/wordAPI"
import { Word } from "../../app/types/types"
import AdminWordsRow from "./AdminWordsRow"

function sortById(a: Word, b: Word){
    return a.id - b.id
}
function sortByEng(a: Word, b: Word){
    return a.eng.localeCompare(b.eng)
}
function sortByRus(a: Word, b: Word){
    return a.rus.localeCompare(b.rus)
}
export default function AdminWords(){
    const {data, isSuccess} = useGetAllWordsQuery()
    const [comparator, setComparator] = useState<any>({fn: sortById, increase: true})
    const sorted = isSuccess 
        ? comparator.increase 
            ? [...data].sort(comparator.fn) 
            : [...data].sort(comparator.fn).reverse()
        : []
    return (
        <Table hoverable={true}>
            <Table.Head>
                <Table.HeadCell className="!p-4"><Checkbox /></Table.HeadCell>
                <Table.HeadCell onClick={()=>setComparator({fn: sortById, increase: !comparator.increase})} className="!px-2">ID</Table.HeadCell>
                <Table.HeadCell onClick={()=>setComparator({fn: sortByEng, increase: !comparator.increase})}>English</Table.HeadCell>
                <Table.HeadCell onClick={()=>setComparator({fn: sortByRus, increase: !comparator.increase})}>Russian</Table.HeadCell>
                <Table.HeadCell>Delete</Table.HeadCell>
            </Table.Head>
            <Table.Body className="divide-y">
                {isSuccess && 
                sorted.map((word: Word, i: number) => {
                        return (
                            <AdminWordsRow key={i} {...word}/>
                        )
                    })
                }
            </Table.Body>
        </Table>
    )
}
