import GroupCard from "../Groups/GroupCard";
import TextCard from "../Texts/TextCard";
import { Progress } from "flowbite-react";
import { useEffect, useState } from "react";
import { getVocabulary } from "../../app/clientAPI/vocabularyAPI";
import getGroupProgress from "../../app/fns/groupFns";
import { useAppSelector } from "../../app/hooks/hooks";
import { WORD_IDS } from "../../app/variables/dbVariables";

export function References(props : { groupId?: number, textId?: number, audioId?: number, videoId?: number}){
    const vocabulary = useAppSelector(getVocabulary)
    const [ word_ids, setWord_ids ] = useState<number[]>([])
    const progress = getGroupProgress(vocabulary, word_ids)
    useEffect(()=>{
        fetch(`http://localhost:3002/groups/${props.groupId || props.textId || props.audioId || props.videoId}`)
        .then( response => {
            return response.json()
        })
        .then(response =>{
            if(response && response.hasOwnProperty(WORD_IDS)){
                setWord_ids( response.word_ids )
            }
        })
        .catch(err => {
            setWord_ids ( [] )
        })
    }, [ vocabulary ])
    return (
        <>
            {/* {! props.groupId && <GroupCard />} */}
            {/* {! props.textId && <TextCard />} */}
            {/* {! props.audioId && <GroupCard />} */}
            {/* {! props.videoId && <GroupCard />} */}

        </>
        // Здесь отдельно карточки с грамматикой если есть
    )
}
