import { useParams } from "react-router-dom"
import { useGetGroupsQuery, useGetOneGroupQuery } from "../../../app/API/groupsAPI"
import { useGetOneTextQuery } from "../../../app/API/textsAPI"
import { useAppSelector } from "../../../app/hooks/hooks"
import { RootState } from "../../../app/store"
import { Group } from "../../../app/types/types"
import GroupCard from "../../GroupCard/GroupCard"

export default function TextPage(){
    const { id } = useParams()
    const { data: text, isSuccess, isError, error } = useGetOneTextQuery(id ? Number(id) : 1)
    // const ref_group = useAppSelector((state: RootState) => state.groups.find((el: Group) => el.id === text.id))

    return(
        <>  
            {isError &&
            <div>
                <h1>Какая-то ошибка...</h1>
            </div>
            }
            {isSuccess &&
            <div>
                {text.content_references?.id_group && 
                <div className='grid grid-cols-1 gap-4 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 xl:grid-cols-5 2xl:grid-cols-5'>
                    <GroupCard {...groups[text.content_references?.id_group]}/>
                </div>
                } 
                <h1>{text.title}</h1>
                <div>{text.text_body}</div>
            </div>
            }
        </>
    )
}
