import { User, Word } from "./types/types";

export function setUserToLocalStorage(user: User){
    localStorage.setItem('id', String(user.id));
    user.user_login && localStorage.setItem('login', user.user_login);
    user.token && localStorage.setItem('token', user.token);
    user.refresh_token && localStorage.setItem('refreshToken', user.refresh_token);
    user.jwtExpire && localStorage.setItem('jwtExpire', String(user.jwtExpire)); //Изменить на нижнее подчеркивание //Уточнить тип
}
export function removeUserFromLocalStorage(){
    localStorage.removeItem('id')
    localStorage.removeItem('login')
    localStorage.removeItem('token')
    localStorage.removeItem('refreshToken')
    localStorage.removeItem('jwtExpire')
}
