import { useEffect } from "react"
import { useParams } from "react-router-dom"
import { useGetGroupsQuery, useGetOneGroupQuery } from "../../../app/API/groupsAPI"
import { getReferences } from "../../../app/API/referencesSlice"
import { useGetOneTextQuery } from "../../../app/API/textsAPI"
import { getOneTextThunk } from "../../../app/API/textSliceAPI"
import { useAppDispatch, useAppSelector } from "../../../app/hooks/hooks"
import { RootState } from "../../../app/store"
import { Group } from "../../../app/types/types"
import GroupCard from "../../GroupCard/GroupCard"
import { References } from "../../References/References"

export default function TextPage(){
    const dispatch = useAppDispatch()
    const { id } = useParams()
    const text = useAppSelector((state: RootState) => state.oneText)
    const references = useAppSelector((state: RootState) => state.references)
    useEffect(()=>{
        if(id && +id !== text.id){
            dispatch(getOneTextThunk(id))
            dispatch(getReferences({item: 'texts', id}))
        }
    }, [id])
    return(
        <>  
            {!text &&
            <div>
                <h1>Какая-то ошибка...</h1>
            </div>
            }
            {text &&
            <div>
                {references && 
                    <References />
                } 
                <h1>{text.title}</h1>
                <div>{text.text_body}</div>
            </div>
            }
        </>
    )
}
