import { Breadcrumb } from "flowbite-react";
import { HomeIcon } from '@heroicons/react/24/solid'
import useBreadcrumbs from "use-react-router-breadcrumbs";
import {Link, useLinkClickHandler, useLocation} from "react-router-dom";
import { useEffect, useState } from "react";
import { useGetGroupsQuery } from "../../app/API/groupsAPI";
import { Group } from "../../app/types/types";

type RouteType = {
    path: string,
    breadcrumb: string
}

export default function(){
    const {data: groups = [], isSuccess} = useGetGroupsQuery()
    const defaultRoutes = [
        { path: "/", breadcrumb: "Главная" },
        { path: "/:Group/English", breadcrumb: "Английский - Русский" },
        { path: "/:Group/Russian", breadcrumb: "Русский - Английский" },
        { path: "/:Group/Spelling", breadcrumb: "По буквам" },
        { path: "/:Group/Auding", breadcrumb: "Аудирование" },
      ];
    const routesFromGroup: RouteType[] = isSuccess ? groups.map(el => ({path: el.title, breadcrumb: el.title_rus})) : []
    const routes = defaultRoutes.concat(routesFromGroup)
    const breadcrumbs = useBreadcrumbs(routes);
    const goWords = useLinkClickHandler('/')
    return (
        <Breadcrumb aria-label="Default breadcrumb example" className="m-2 my-4">
            {breadcrumbs.map(({ breadcrumb, match }) => {
                return (
                    <Breadcrumb.Item icon={match.pathname === '/' ? HomeIcon : undefined} key={match.pathname}>  
                        <Link to={match.pathname} className="mt-20">
                            {breadcrumb}
                        </Link>{/* При клике страница перезагружается */}
                    </Breadcrumb.Item>
                )
            })}

        </Breadcrumb>
    )
}
