import { createAsyncThunk, createSlice } from '@reduxjs/toolkit'
import { Text, TextTitle } from '../types/types'

export const getAllGlobalTextsTitlesThunk = createAsyncThunk<TextTitle[]>(
    'Thunk: getAllGlobalTextTitles',
    async function() {
        const response = await fetch(`http://localhost:3002/texts/titles`)
        const data = await response.json()
        return data
    }
)

const initialState: TextTitle[] = []
export const textsSlice = createSlice({
    name: 'textsSlice',
    initialState: initialState,
    reducers: {},
    extraReducers: (builder) => {
        builder.addCase(getAllGlobalTextsTitlesThunk.fulfilled, (_, action) => action.payload)
    }
})
export const getOneTextThunk = createAsyncThunk<Text, number>(
    'Thunk: getOneText',
    async function(id: number | string) {
        const response = await fetch(`http://localhost:3002/texts/${id}`)
        const data = await response.json()
        return data
    }
)
const oneTextInitialState: Text = {
    id: 0,
    title: 'Ошибка!',
    img: '404.jpg',
    text_body: 'Возникла ошибка, вероятно на стороне сервера.',
    content_references: null
}
export const oneTextSlice = createSlice({
    name: 'oneTextSlice',
    initialState: oneTextInitialState,
    reducers: {},
    extraReducers: (builder) => {
        builder.addCase(getOneTextThunk.fulfilled, (_, action) => action.payload)
    }
})
